import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
    <div>
<meta httpEquiv="Access-Control-Allow-Origin" content="*"></meta>
        <Link href="/about">
            <a>About Page</a>
        </Link>
        <p>{props.data}</p>
    </div>
);

Index.getInitialProps = async function () {
    const res = await fetch('https://dy-api.com/v2/serve/user/choose', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            "DY-API-Key": "b6e390c7cd6f72a0fd39c57ea40a8c1508ec31d28ae402a45280ba3c2afbc62e"
        },
        body: JSON.stringify({
            "user": { "id": "saul" },
            "sessionId": "6eab6b44a4cbd563376663e8b5f24e92",
            "selector": { "names": ["recs"] },
            "context": {
                "page": {
                    "type": "OTHER",
                    "data": [],
                    "location": "www.example.com",
                    "locale": "en_US"
                }
            }
        })
    }).then(r => {
        return r;
    })


    const data = await res.json();

    return {
        data
    };
};

export default Index;
