import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
    <div>
        <Link href="/about">
            <a>About Page</a>
        </Link>
        <p>{props.data.choices[0].variations[0].payload.data.color}</p>
    </div>
);

Index.getInitialProps = async function () {
    const res = await fetch('https://dy-api.com/v2/serve/user/choose', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            "DY-API-Key": "17e66fe84f6fe600e2d250becdf396eaf974d3c93fefc9bd6cfd96a86577a1aa"
        },
        body: JSON.stringify({
            "user": { "id": "saul" },
            "sessionId": "6eab6b44a4cbd563376663e8b5f24e92",
            "selector": { "names": ["Saul-Campaign"] },
            "context": {
                "page": {
                    "type": "OTHER",
                    "data": [],
                    "location": "www.example.com",
                    "locale": "en_US"
                }
            }
        })
    }).then(r => {
        return r;
    })


    const data = await res.json();

    return {
        data
    };
};

export default Index;
